import { ADD_INPUT_DATA, POST_INPUT_DATA } from "../ActionTypes";

export const addInputData = (nickName, content) =>  ({ type: ADD_INPUT_DATA, payload: { nickName, content } });

export const postInputData = (nickName, content) => ({ type: POST_INPUT_DATA, payload: { nickName, content }})
