import React, { useState, useEffect } from 'react';
import axios from "axios"

const Main = () => {

    const [data, setDate] = useState([]);
    const [inputData, setInputDate] =useState({
        nickName:"",
        content:"",
    })

    useEffect(() => {

        async function responseDelay () {
            await dateGet()
        }

        responseDelay();

    },[])

    const inputChange = (e) => {
        setInputDate( prevState =>({
            prevState,
            [e.name] : e.target.value,
        }))
    }

    const dateGet = async () => {

        try {
            const response = await axios.get("http://dauth.daios.net/v1/boards",{
                headers:{
                    "x-access-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI1ZWUyMjJjNmFmOTU2MjAwMTFiNmI5YmEiLCJpYXQiOjE1OTE4NzgzNDIsImV4cCI6MTU5MTk2NDc0Mn0.pdSl7F3P9OIiTjAVynb_wfgyw9anNxQ7RiaJcIONQ6I",
                    "Content-Type":"application/json"
                }
            });

            const data = await response.data.data;

            return setDate(data);

        } catch (error) {

            console.log('Error', error.message);
        }
    }

    const elementRender = () => {

            data.map((item,idx) => {

                console.log(item);

                return <li key={idx}>
                            <span>{item.__v}</span>
                            <em></em>
                            <p>{item.content}</p>
                            <span></span>
                        </li>
            });

    }

    return (
        <div>
            <div>
                <input onChange={inputChange} name={"nickName"} value={inputData.nickName} type="text"/>
                <input onChange={inputChange} name={"content"} value={inputData.content} type="text"/>
            </div>

            <ul>
                {data.map((item,idx) => {

                    return <li key={idx}>
                                <span>{item._id}</span>
                                <span>{item.nickName}</span>
                                <p>{item.content}</p>
                                <em>{item.createdAt}</em>
                                <span>{item.__v}</span>
                            </li>
                })}
            </ul>
        </div>
    );
};

export default Main;
