import React, { useState, useEffect } from 'react';
import styled from "styled-components"
import axios from "axios"

const Main = () => {

    const [data, setDate] = useState([]);
    const [inputData, setInputDate] = useState({
        nickName:"",
        content:"",
    })

    useEffect(() => {

        async function responseDelay () {
            await dateGet()
        }

        responseDelay();

    },[])

    const inputChange = (e) => {

        const inputTarget = e.target;
        setInputDate((prev) => {

            return {
                ...prev,
                [inputTarget.name] : inputTarget.value
            };
        })
    }

    const inputSubmit = () => {
        console.log(inputData);
    }

    const dateGet = async () => {

        try {
            const response = await axios.get("http://dauth.daios.net/v1/boards",{
                headers:{
                    "x-access-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI1ZWUyMjJjNmFmOTU2MjAwMTFiNmI5YmEiLCJpYXQiOjE1OTE4NzgzNDIsImV4cCI6MTU5MTk2NDc0Mn0.pdSl7F3P9OIiTjAVynb_wfgyw9anNxQ7RiaJcIONQ6I",
                    "Content-Type":"application/json"
                }
            });

            const data = await response.data.data;

            return setDate(data);

        } catch (error) {

            console.log('Error', error.message);
        }
    }

    return (
        <div>
            <form onSubmit={inputSubmit}>
                <ol>
                    <li>별명: <Input placeholder={"별명"} onChange={(e) => inputChange(e)} name="nickName" value={inputData.nickName} type="text"/></li>
                    <li> 내용: <Input placeholder={"내용"}  onChange={inputChange} name="content" value={inputData.content} type="text"/></li>
                </ol>
                <button type="button">제출</button>
            </form>

            <ul>
                {data.map((item,idx) => {

                    return <li key={idx}>
                                <span>{item._id}</span>
                                <span>{item.nickName}</span>
                                <p>{item.content}</p>
                                <em>{item.createdAt}</em>
                                <span>{item.__v}</span>
                            </li>
                })}
            </ul>
        </div>
    );
};

const Input = styled.input`
  width: calc(550px - 60px);
  border: 1px solid #333;
`;

export default Main;
