import React from 'react';
import styled from "styled-components";

const Form = (props) => {

    const [creatPostData, setCreatePostdata] = useState({
        nickName:"",
        content:"",
    })

    const inputChange = (e) => {
        const inputTarget = e.target;
        setCreatePostdata((prev) => ({
            ...prev,
            [inputTarget.name] : inputTarget.value
        }));
    }

    return (
        <div>
           <form onSubmit={props.creatPost} method="post"> 
                <ol>
                    <li>별명: <Input placeholder={"별명"} onChange={props.inputChange} name="nickName" value={creatPostData.nickName} type="text"/></li>
                    <li> 내용: <Input placeholder={"내용"}  onChange={props.inputChange} name="content" value={creatPostData.content} type="text"/></li>
                </ol>
                <button type="submit">제출</button>
            </form>

        </div>
    );
};

const Input = styled.input`
  width: calc(550px - 60px);
  border: 1px solid #333;
`;

export default Form;
