import { ADD_INPUT_DATA, POST_INPUT_DATA } from "../constants/ActionTypes"

const initialState = {
    input: {
        nickName: "",
        content: ""
    },
    list:[
        {
        _id: 0,
        content: "",
        nickName: "",
        createdAt: "",
        __v: 0
        }

    ]
}

export default function addInputData (state = initialState, action) {

    console.log(action);


    switch (action.type) {
        case ADD_INPUT_DATA:

            return {
                ...state,
                input: action.payload
            };

        case POST_INPUT_DATA:

            return {
                ...state,
                input: action.payload
            };         
        default:
            return state;
    }
}
