import React, { useState, useEffect } from 'react';
import styled from "styled-components"
import axios from "axios"


const X_ACCESS_TOKEN = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI1ZWUyMjJjNmFmOTU2MjAwMTFiNmI5YmEiLCJpYXQiOjE1OTE4NzgzNDIsImV4cCI6MTU5MTk2NDc0Mn0.pdSl7F3P9OIiTjAVynb_wfgyw9anNxQ7RiaJcIONQ6I';
const requestInstance = axios.create({
    headers:{
        "x-access-token": X_ACCESS_TOKEN,
        "Content-Type":"application/json"
    },
    baseURL: 'http://dauth.daios.net/v1',
    timeout: 3000,
    timeoutErrorMessage: '다시 시도 바랍니다.',
});




const Main = () => {

    const [getPostData, setGetPostData] = useState([]);
    const [creatPostData, setCreatePostdata] = useState({
        nickName:"",
        content:"",
    })

    const post = { 
        create : async (e) => {
            e.preventDefault();
            try {
                const response = await requestInstance.post("/boards", creatPostData);
                setCreatePostdata({
                    nickName:"",
                    content:"",                
                });
                //let createRequestResult = await response.data;
                //setGetPostData(getPostData.concat(createRequestResult.data));
                //return createRequestResult;

            } catch(error) {
                console.log('Error', error.message);            
            }
        },
        show : async () => {
            try {
                const response = await requestInstance.get("/boards");
                return setGetPostData(await response.data.data);
            } catch (error) {
                console.error('Error', error.message);
            }
        }
    };

    useEffect(() => {
        async function responseDelay () {
            await post.show();
        }
        responseDelay();
    },[post])

    const inputChange = (e) => {
        const inputTarget = e.target;
        setCreatePostdata((prev) => ({
            ...prev,
            [inputTarget.name] : inputTarget.value
        }));
    }

    return (
        <div>
            <form onSubmit={post.create} method="post"> 
                <ol>
                    <li>별명: <Input placeholder={"별명"} onChange={inputChange} name="nickName" value={creatPostData.nickName} type="text"/></li>
                    <li> 내용: <Input placeholder={"내용"}  onChange={inputChange} name="content" value={creatPostData.content} type="text"/></li>
                </ol>
                <button type="submit">제출</button>
            </form>

            <ul>
                {getPostData.map((item,idx) => (
                <li key={idx}>
                    <span>{item._id}</span>
                    <span>{item.nickName}</span>
                    <p>{item.content}</p>
                    <em>{item.createdAt}</em>
                    <span>{item.__v}</span>
                </li>))}
            </ul>
        </div>
    );
};

const Input = styled.input`
  width: calc(550px - 60px);
  border: 1px solid #333;
`;

export default Main;
