import React,{useState} from 'react';
import styled from "styled-components";

const Form = (props) => {

    const inputChange = (e) => {

        const target = e.target;

        props.onChange(target.name, target.value)
    }

    const createPost = (e) => {
        e.preventDefault();

        console.log(props.createPost);

        props.createPost(props.value);
    }

    return (
        <div>
           <form onSubmit={createPost} method="post"> 
                <ol>
                    <li>별명: <Input placeholder={"별명"} onChange={inputChange} name="nickName" value={props.value.nickName} type="text"/></li>
                    <li> 내용: <Input placeholder={"내용"}  onChange={inputChange} name="content" value={props.value.content} type="text"/></li>
                </ol>
                <button type="submit">제출</button>
            </form>

        </div>
    );
};

const Input = styled.input`
  width: calc(550px - 60px);
  border: 1px solid #333;
`;

export default Form;
