import { ADD_INPUT_DATA } from "../ActionTypes"

const initialState = {
        nickName: "",
        content: ""
}

export default function addInputData (state = initialState, action) {

    console.log("types",action.type);

    switch (action.type) {
        case ADD_INPUT_DATA:

            return {
                ...state,
                input: action.payload
            };

        default:
            return state;
    }
}
