import React from 'react';
import axios from "axios"
import { useState } from 'react';
import { useEffect } from 'react';

const Main = () => {

    const [data, setDate] = useState([]);

    useEffect(() => {

        async function responseDelay () {
            await dateGet()
        }

        responseDelay();

    },[])

    const dateGet = async () => {

        try {
            const response = await axios.get("http://dauth.daios.net/v1/boards",{
                headers:{
                    "x-access-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VySWQiOiI1ZWUyMjJjNmFmOTU2MjAwMTFiNmI5YmEiLCJpYXQiOjE1OTE4NzgzNDIsImV4cCI6MTU5MTk2NDc0Mn0.pdSl7F3P9OIiTjAVynb_wfgyw9anNxQ7RiaJcIONQ6I",
                    "Content-Type":"application/json"
                }
            });

            const data = await response.data.data;

            return setDate(data);

        } catch (error) {

            console.log('Error', error.message);
        }
    }

    const elementRender = () => {

        console.log(data.length);

        console.log(data);


        if(data.length > 0) {
            data.map((item,idx) => {

                console.log(item);

                return <li key={idx}>
                            <span>item.__v</span>
                            <em></em>
                            <p>{item.content}</p>
                            <span></span>
                        </li>
            });
        }

    }

    return (
        <div>
            <ul>
                {elementRender()}
            </ul>
        </div>
    );
};

export default Main;
