import { ADD_INPUT_DATA } from "../ActionTypes"

const initialState = {
    nickName: "",
    content: ""
}

export default function addInput (state = initialState, action) {

    console.log("types",action.type);

    switch (action.type) {
        case ADD_INPUT_DATA:

            return {
                ...state,
                [action.payload.nickName]:action.payload.content
            };

        default:
            return state;
    }
}
